mapper.insert:
INSERT INTO BOARD (
    mem_id, board_title, board_content, original_fileName, stored_fileName
) VALUES (
    #{mem_id}, 
    #{board_title}, 
    #{board_content},   
    #{original_fileName, jdbcType=VARCHAR},
    #{stored_fileName, jdbcType=VARCHAR}
)

mapper.update:
UPDATE BOARD
SET deleted_at = SYSDATE
WHERE board_seq = #{board_seq}

mapper.select:
SELECT board_seq, board_title, board_content, mem_id, created_at, updated_at, board_views, original_fileName, stored_fileName
FROM BOARD
WHERE board_seq = #{board_seq}

mapper.update:
UPDATE BOARD 
SET board_views = board_views + 1 
WHERE board_seq = #{board_seq}
