mapper.insert:
INSERT INTO MEMBER (
    mem_id, mem_pw, mem_name, mem_birth, mem_phone, mem_role, mem_email
) VALUES (
    #{mem_id}, #{mem_pw}, #{mem_name},
    TO_DATE(#{mem_birth}, 'YYYY-MM-DD'),
    #{mem_phone}, 1, #{mem_email}
)

mapper.select:
SELECT mem_id, mem_pw, mem_name, mem_phone, mem_email, mem_role
FROM MEMBER
WHERE mem_id = #{mem_id}
